function debounce(func, timeout = 1000) {
  let timer;
  return (...args) => {
    clearTimeout(timer);
    timer = setTimeout(() => {
      func.apply(this, args);
    }, timeout);
  };
}

function showTableLoader(processing) {
  if (processing) {
    $(document).find(".overlay-spinner").show();
  } else {
    $(document).find(".overlay-spinner").hide();
  }
}
